        }
        else{
          return(collected_kurs_all)
        }
      }
    }
    else if(perspective == "Both"){
      bank_collected_kurs_buy <- collected_kurs %>% select(bank_name, symbol_name, buy_today) %>% spread(symbol_name, buy_today)
      bank_collected_kurs_buy["price_type"] <- "Buy"
