quality_control_analysis <- function(data, value_col, numerical_group_col="",
                                     include_nonconforming=FALSE,
                                     size_nonconforming_col="",
                                     logical_nonconforming_col="",
                                     new_data=data.frame(), 
                                     std_value_warning = 0,
                                     qcc_group_train_prop=0.7,
