total_object_subspace_clustered <- function(obj, df_ref){
  xi <- 0
  for(j in 1:length(obj)){
    xi <- xi + length(obj[[j]]$objects)
  }
  if(xi != nrow(df_ref)){
    writeLines(paste0("Row Clustered by Sub Spaces: ", xi))
