    not_clustered <- nrow(df_ref) - xi
    writeLines(paste0("Row Not Clustered: ", not_clustered))
    writeLines(paste0("Percentages Not Clustered: ", round(not_clustered/nrow(df_ref) * 100, 4), "%"))
  }
}

