        writeLines(paste("Execute Model ",exec_model,"of",nrow(caret_topepo)))
        dependent_var <- strsplit(model_formula, " ~ ")[[1]][1]
        independent_var <- colnames(df)[which(colnames(df) != dependent_var)]
        predictor_lists <- list()
