        exponent_stringeval = paste0(exponent_stringeval, "~ exp(x", v, "), ")
      }
      else{
        exponent_stringeval = paste0(exponent_stringeval, "~ exp(x", v, ")) ")
      }
    }
    s <- deltamethod(eval(parse(text=exponent_stringeval)), 
                     coef(pos.model), robust_cov)
    
    #Drop Old P Values and Transform to Exponent values
