        groupby_result <- groupby_result %>% arrange(desc(!!enquo_arrange_col))
      }else if(arrange_priority == "bottom"){
        groupby_result <- groupby_result %>% arrange(!!enquo_arrange_col)
      }
    }
    if(convert_df){
