    }
    else if(year_num %% 10 == 6){
      df$chn_stem[h] <- "Yang"
      df$chn_branch[h] <- "Fire"
    }
    else if(year_num %% 10 == 7){
      df$chn_stem[h] <- "Yin"
      df$chn_branch[h] <- "Fire"
    }
    else if(year_num %% 10 == 8){
      df$chn_stem[h] <- "Yang"
      df$chn_branch[h] <- "Earth"
    }
    else if(year_num %% 10 == 9){
      df$chn_stem[h] <- "Yin"
      df$chn_branch[h] <- "Earth"
    }
    which_zodiac_sign <- (((year_num - 1924) %% 60) + 1)
