        datm <- as.matrix(subset_data)
        obj_lm_fit <- .lm.fit(cbind(1,datm[,-1]), datm[,1])
        obj_lm_fit_r2 <- rsquared(obj_lm_fit, cbind(1,datm[,-1]), datm[,1])
        obj_lm_fit_r2adj <- adj_rsquared_list(obj_lm_fit, cbind(1,datm[,-1]), datm[,1])
