            model_formula <- as.formula(paste0(dependent_col, " ~ ", paste0(all_variable, collapse= " + ")))
            eval_transform <- paste0("data_transformed[[all_indep_col[x]]] <- ",
                                     apply_transformation[w-1],"(data_transformed[[all_indep_col[x]]])")
