                obj_lm_fit_r2adj <- adj_rsquared_list(obj_lm_fit, cbind(1,datm[,-1]), datm[,1])
                
                r2_df <- data.frame(Model_name = "Model1", transformed_columns = colnames(data_transformed)[v], 
                                    transform_function = apply_transformation[w],
