                                                   var_transformation = apply_transformation[w-1],
                                                   aic = stats::AIC(lm_model), 
                                                   bic = stats::BIC(lm_model),
                                                   neg_loglikelihood = as.numeric(stats::logLik(lm_model)),
                                                   model_success = "polr"))
              }
              
              data_transformed <- data
            }
            else if(use_all_method == "clm"){
