    else if(method == "KPCA"){
      technic_name <- "Kernel PCA"
      dimen <- dimRed::AutoEncoder()
      if(!no_message){
        writeLines('Kernel PCA is a nonlinear extension of PCA using kernel methods.')
      }
      dimension_params <- dimen@stdpars
      dimension_params$ndim <- n_dimension
