            }
          }
          
          for(vars in 1:length(var_to_select)){
            add_colname <- paste0("Var",vars)
            if(transform_to_select[vars] != "none"){
              overlap_reduction_df[[add_colname]] <- paste0(transform_to_select[vars],"_",var_to_select[vars])
