    new_value_to <- c()
    new_datetime_change <- c()
    new_day_change <- c()
    new_day_string <- c()
    new_value_change <- c()
    combination_occurence <- c()
    
    for(b in 1:nrow(value_time_combination)){
      value_rng <- as.numeric(unlist(strsplit(value_time_combination$value_change_rng[b],"-")))
