      if(check_from[1] < check_to[1]){ #Bullish rank
        value_to <- sort(unique(next_zigzag_df$new_value_to),decreasing = T)
      }else if(check_from[1] > check_to[1]){ #Bearish rank
        value_to <- sort(unique(next_zigzag_df$new_value_to))
      }
