    cpr_to_fill_factor <- ifelse(cpr_to_fill < min(cpr_from_unique), unique_cpr_prevar[w_min_cf],
                                 ifelse(cpr_to_fill > max(cpr_to_unique), unique_cpr_prevar[w_max_ct], NA))
    if(debug) print(cpr_to_fill_factor)
