      for(b in 1:length(cpr_to_fill)){
        if(is.na(cpr_to_fill_factor[b]) && (cpr_to_fill[b] >= cpr_from_unique[a] && cpr_to_fill[b] <= cpr_to_unique[a])){
          cpr_to_fill_factor[b] <- unique_cpr_prevar[a]
        }
